    private void btnDecimalActionPerformed(java.awt.event.ActionEvent evt) {                                           
        // Parse Text to see if decimal point is already there
        String currentTxt = txtDisplay.getText();
        boolean hasDecimalAlready = false;
        if ( currentTxt.indexOf(".") != -1 ) {
            hasDecimalAlready = true;
        }
        // Add Decimal Point only if it's not already there
        if (!hasDecimalAlready) {
            String btnText = txtDisplay.getText() + ".";
            txtDisplay.setText(btnText);
        }
    }                                          

